import React, { useState, useEffect } from 'react';
import './App.css';
import Post from './Post';
import { db } from './firebase';

function App() {
  const [posts, setPosts] = useState([]);
  useEffect(() => {
    // this is where code runs
      db.collection('post').onSnapshot(snapshot =>{
        //every time new post is added , this code fires....
        setPosts(snapshot.docs.map(doc=>doc.data()));
      })
  }, []);

  return (
    <div className="App">

        <div className="app_header">
          <img className="app_headerImage"
          src="https://www.instagram.com/static/images/web/mobile_nav_type_logo.png/735145cfe0a4.png"
          alt=""
          /> 
        <h1>
          Hello,  Let's build an INSTAGRAM CLONE  </h1>
         {  
           posts.map(post => (
           <Post username={post.username} caption={post.caption} imageurl={post.imageurl}/>
         ))

        }

       // <Post username="Aman Kumar : " imageurl="https://images.unsplash.com/photo-1568605117036-5fe5e7bab0b7?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=500&q=60"/>
          <Post />
          <Post />
                  </div>



      {/*header*/}

      {/*Posts*/}
      {/*Posts*/}




       </div>

      );
}

export default App;
